import React, { useState } from 'react';
import './eliminar.css'; 

const Eliminar = ({ data }) => {
  const [selectedItems, setSelectedItems] = useState([]);
  const [selectAll, setSelectAll] = useState(false);

  const toggleSelection = (item) => {
    if (selectedItems.includes(item)) {
      setSelectedItems(selectedItems.filter((selected) => selected !== item));
    } else {
      setSelectedItems([...selectedItems, item]);
    }
  };

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedItems([]);
    } else {
      setSelectedItems(data.map((item) => item.id));
    }
    setSelectAll(!selectAll);
  };

  const handleDelete = () => {
    if (selectedItems.length === 0) {
      alert('No se han seleccionado elementos para eliminar.');
      return;
    }

    fetch('http://localhost:7777/api/Indicadores', {
      method: 'DELETE', 
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ selectedItems }),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error al eliminar elementos');
        }
      })
      .then((data) => {
        alert('Elementos eliminados con éxito.');
        setSelectedItems([]);
      })
      .catch((error) => {
        console.error('Error al eliminar elementos:', error);
        alert('Error al eliminar elementos. Consulta el registro de la consola para obtener más detalles.');
      });
  };


  return (
    <div className="eliminar-container">
      <h2>Eliminar Elementos</h2>
      <button onClick={handleSelectAll}>
        {selectAll ? 'Deseleccionar todo' : 'Seleccionar todo'}
      </button>
      <button onClick={handleDelete}>Eliminar seleccionados</button>
      <table className="eliminar-tabla">
        <thead>
          <tr>
            <th>Seleccionar</th>
            <th>Indicador</th>
            <th>Descripción</th>
            <th>...</th>
          </tr>
        </thead>
        <tbody>
        {data && data.map((item) => (
  <tr key={item.id} className={selectedItems.includes(item.id) ? 'selected' : ''}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.id)}
                  onChange={() => toggleSelection(item.id)}
                />
              </td>
              <td>{item.indicador}</td>
              <td>{item.descripcion}</td>
              <td>...</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Eliminar;
