import React, { useState, useEffect } from 'react';
import './eliminar.css';

const Eliminar = () => {
  const [selectedItems, setSelectedItems] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const toggleSelection = (item) => {
    if (selectedItems.includes(item.id)) {
      setSelectedItems(selectedItems.filter((selected) => selected !== item.id));
    } else {
      setSelectedItems([...selectedItems, item.id]);
    }
  };

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedItems([]);
    } else {
      setSelectedItems(data.map((item) => item.id));
    }
    setSelectAll(!selectAll);
  };

  const handleDelete = () => {
    if (selectedItems.length === 0) {
      alert('No se han seleccionado elementos para eliminar.');
      return;
    }

    const itemsToDelete = selectedItems;

    setLoading(true);

    setTimeout(() => {
      const updatedData = data.filter((item) => !itemsToDelete.includes(item.id));

      setData(updatedData);
      setSelectedItems([]);
      setLoading(false);
      alert('Elementos eliminados con éxito.');
    }, 1000); // Simulación de eliminación (reemplaza con tu lógica de API real)
  };

  useEffect(() => {
    setLoading(true);
    // Reemplaza 'URL_API' con la URL de tu API real
    fetch('URL_API')
      .then((response) => response.json())
      .then((fetchedData) => {
        setData(fetchedData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error al obtener los datos:', error);
        setLoading(false);
      });
  }, []);

  const getCircleColor = (cumplimiento) => {
    // Define la lógica para obtener el color del círculo según el cumplimiento.
    // Reemplaza con tu propia lógica.
    return cumplimiento >= 50 ? 'green' : 'red';
  };

  return (
    <div className="eliminar-container">
      <h2>Eliminar Elementos</h2>
      <button onClick={handleSelectAll}>
        {selectAll ? 'Deseleccionar todo' : 'Seleccionar todo'}
      </button>
      <button onClick={handleDelete}>Eliminar seleccionados</button>
      <table className="eliminar-tabla">
        <thead>
          <tr>
            <th>Seleccionar</th>
            <th>Indicador</th>
            <th>Descripción</th>
            <th>Categoría</th>
            <th>Fecha de Inicio</th>
            <th>Fecha de Terminación</th>
            <th>Formula</th>
            <th>Frecuencia</th>
            <th>Cumplimiento</th>
            <th>Área</th>
            <th>Acción</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id} className={selectedItems.includes(item.id) ? 'selected' : ''}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.id)}
                  onChange={() => toggleSelection(item)}
                />
              </td>
              <td>{item.indicador}</td>
              <td>{item.descripcion}</td>
              <td>{item.categoria}</td>
              <td>{item.fecha_inicio}</td>
              <td>{item.fecha_terminacion}</td>
              <td>{item.formula}</td>
              <td>{item.frecuencia}</td>
              <td>
                {loading ? (
                  "Cargando..."
                ) : (
                  <div className="circle-loader">
                    <svg width="60" height="60">
                      <circle
                        className="circle"
                        cx="30"
                        cy="30"
                        r="27"
                        stroke={getCircleColor(item.cumplimiento)}
                        strokeWidth="7"
                        fill="none"
                        strokeDasharray="170"
                        strokeDashoffset={(170 * (100 - item.cumplimiento)) / 100}
                      />
                      <text x="30" y="30" textAnchor="middle" dy="0.3em" className="percentage">
                        {`${item.cumplimiento}%`}
                      </text>
                    </svg>
                  </div>
                )}
              </td>
              <td>{item.area}</td>
              <td>
                <button onClick={() => handleDelete([item.id])}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Eliminar;
