import React, { useState } from 'react';
import Modal from './Modal'; // Asegúrate de importar el componente Modal

const Panel = () => {
  const [elementoSeleccionado, setElementoSeleccionado] = useState(null);

  // Datos de ejemplo. Debes reemplazar esto con tus propios datos.
/*   const datos = [
    {
      _id: 1,
      indicador: 'Indicador 1',
      descripcion: 'Descripción 1',
      categoria: 'Categoría 1',
      fecha_inicio: 'Fecha de Inicio 1',
      fecha_terminacion: 'Fecha de Terminación 1',
      formula: 'Fórmula 1',
      frecuencia: 'Frecuencia 1',
      area: 'Área 1',
    },
    {
      _id: 2,
      indicador: 'Indicador 2',
      descripcion: 'Descripción 2',
      categoria: 'Categoría 2',
      fecha_inicio: 'Fecha de Inicio 2',
      fecha_terminacion: 'Fecha de Terminación 2',
      formula: 'Fórmula 2',
      frecuencia: 'Frecuencia 2',
      area: 'Área 2',
    },
  ]; */

  const abrirModal = (item) => {
    setElementoSeleccionado(item);
  };

  const cerrarModal = () => {
    setElementoSeleccionado(null);
  };

  return (
    <div className="app">
      <h1 className="pagina">Panel de Indicadores</h1>
      <table className="tabla">
        <thead>
          {/* Encabezado de la tabla aquí */}
        </thead>
        <tbody>
          {datos.map((item) => (
            <tr
              key={item._id}
              className="rectangulo"
              onClick={() => abrirModal(item)}
            >
              <td colSpan={9}>
                <div className="informacion">
                  <div className="info1">{item.indicador}</div>
                  <div className="info2">{item.descripcion}</div>
                  <div className="info3">{item.categoria}</div>
                  <div className="info4">{item.fecha_inicio}</div>
                  <div className="info5">{item.fecha_terminacion}</div>
                  <div className="info6">{item.formula}</div>
                  <div className="info7">{item.frecuencia}</div>
                  <div className="info9">{item.area}</div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {elementoSeleccionado && (
        <Modal data={elementoSeleccionado} onClose={cerrarModal} />
      )}
    </div>
  );
};

export default Panel;
